@use '@styles/variables' as *;

.calendar-mobile {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  padding: 1rem;
  font-family: $font-family-default;
  box-sizing: border-box;
  display: flex; /* Activar Flexbox */
  flex-direction: column; /* Organizar en columna */
  gap: 16px;
  overflow: hidden; /* Evita desbordes */

  h4 {
    margin: 0;
  }
  body {
    margin: 0;
  }

  &__header {
    text-align: center; /* Centra el texto en el header */
    font-size: 1.5rem;
    flex-shrink: 0; /* El header no colapsa */
  }

  &__body {
    display: flex;
    flex-direction: column;
    flex: 1; /* El body ocupa el espacio restante */
    gap: 2rem;

    .calendar-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      font-size: 1rem;
      font-weight: bold;
      padding-bottom: 1rem;

      button {
        background-color: transparent;
        border: none;
        font-size: 1rem;
        cursor: pointer;
        color: $white-70;

        &:hover {
          color: $white-70;
        }
      }
    }
    .calendar-days {
      display: grid;
      grid-template-columns: repeat(7, 1fr);
      gap: 0.5rem;
      text-align: center;
      font-weight: bold;
      color: $white-80;
      margin-bottom: 0.5rem;
    }

    .calendar-dates {
      display: grid;
      grid-template-columns: repeat(7, 1fr);
      gap: 0.5rem;
      text-align: center;

      .calendar-date {
        padding: 0.5rem;
        border-radius: 4px;
        background-color: $white-50;
        cursor: pointer;
        transition: background-color 0.1s, color 0.1s;

        @media screen and (max-width: 320px) {
          padding: 0.5rem 0.25rem;
        }

        &.empty {
          background-color: transparent; // Los d√≠as vac√≠os no tienen fondo
          cursor: default; // Deshabilita el clic en los d√≠as vac√≠os
        }

        &:hover:not(.empty) {
          background-color: $white-70;
          color: white;
        }

        &.selected {
          background-color: $primary;
          font-weight: 600;
          color: white;
        }
      }
    }
  }

  &__footer {
    display: flex;
    gap: 0.5rem;
    flex-direction: row;
    width: 100%;
    flex-shrink: 0; /* El footer no colapsa */

    .button-wrapper {
      flex: 1; /* Cada bot√≥n ocupa el 50% del ancho */
    }
  }
}

