import { AfterViewInit, Component, ElementRef, Input, OnChanges, OnInit, Renderer2, SimpleChanges } from '@angular/core';
import { IconService } from '../../../services/icon.service';
import { SafeHtmlPipe } from '../../../pipes/safe-html.pipe';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-icon',
  templateUrl: './icon.component.html',
  styleUrls: ['./icon.component.scss'],
  imports: [SafeHtmlPipe, CommonModule],
})

export class IconComponent implements AfterViewInit, OnChanges {
  @Input() iconName: string = '';
  @Input() fillColor: string = 'currentColor';
  @Input() size: number = 16;
  iconSvg: string = '';
  private observer: MutationObserver | null = null;

  constructor(
    private iconService: IconService,
    private elementRef: ElementRef,
    private renderer: Renderer2
  ) { }


  ngAfterViewInit(): void {
    if (this.iconName) {
      this.iconService.getIcon(this.iconName).subscribe(svg => {
        if (svg) {
          this.iconSvg = svg;
          this.observeSvgChanges(); // Observa cuando el SVG est√© en el DOM
        } else {
          console.error('SVG not found in library for icon:', this.iconName);
        }
      });
    }
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['fillColor'] && !changes['fillColor'].firstChange) {
      this.applyFillColor(); // Aplica el nuevo color si cambia `fillColor`
    }
  }

  private observeSvgChanges(): void {
    // Crea un observer para detectar cambios en el DOM
    this.observer = new MutationObserver(() => {
      const svgElement = this.elementRef.nativeElement.querySelector('svg');
      if (svgElement) {
        this.applyFillColor(); // Aplica el color una vez que el SVG est√° disponible
        this.observer?.disconnect(); // Det√©n la observaci√≥n
      }
    });

    // Comienza a observar cambios en el DOM del componente
    this.observer.observe(this.elementRef.nativeElement, { childList: true, subtree: true });
  }

  private applyFillColor(): void {
    const svgElement = this.elementRef.nativeElement.querySelector('svg');
    if (svgElement) {
      this.renderer.setAttribute(svgElement, 'fill', this.fillColor);
    }
    // console.log(svgElement)
  }

  ngOnDestroy(): void {
    this.observer?.disconnect(); // Limpieza del observer
  }

}

import { Component, EventEmitter, Input, OnChanges, OnInit, Output, SimpleChanges } from '@angular/core';
import { CommonModule } from '@angular/common';
import { IconComponent } from '../icon/icon.component';
import { InputType } from '../../../enums/input-type.enum';

@Component({
  selector: 'lib-input-simple',
  imports: [CommonModule, IconComponent],
  templateUrl: './input-simple.component.html',
  styleUrl: './input-simple.component.scss'
})
export class InputSimpleComponent implements OnChanges, OnInit{

  ngOnInit(): void {
    this.helperTextDefault = this.helperText
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['type'] && !changes['type'].firstChange) {
      const valueAsString = this.value != null ? String(this.value) : ''; // Asegura que el valor sea un string
      this.validateInputByType(valueAsString); // Pasar el valor convertido a string
    }
  }

  @Input() type!: InputType; // Tipo por defecto
  @Input() value?: string | number | null;
  @Input() label?: string;
  @Input() helperText?: string;
  @Input() placeholder?: string;
  @Input() isDisabled?: boolean;
  @Input() isFocus?: boolean;
  @Input() isReadonly?: boolean;
  @Input() isError?: boolean;
  @Input() maxLength?: number;

  helperTextDefault?: string;

  @Output() valueChange = new EventEmitter<any>();

  onInputHandler(event: Event): void {
    const input = (event.target as HTMLInputElement).value;
    this.valueChange.emit(input);
    this.validateInputByType(input)
  }

  onFocusHandler(event: Event): void {
    this.isFocus = true;
    // console.log('Focus Input')
  }

  onBlurHandler(event: Event): void {
    // console.log('Blur Out Input')
    this.isFocus = false;
  }

  onKeydown(event: KeyboardEvent, inputRef: HTMLInputElement): void {
    if (event.key === 'Enter') {
      inputRef.blur(); // Quita el foco del input
    } else if (event.key === 'Escape') {
      inputRef.blur(); // Quita el foco del input
    }
  }

  validateInputByType(value: string): void {
    switch (this.type) {
      case InputType.TEXT:
        this.handleTextValidation(value);
        break;
      case InputType.EMAIL:
        this.handleEmailValidation(value);
        break;
      case InputType.PASSWORD:
        this.handlePasswordValidation(value);
        break;
      default:
        console.warn(`Validation not implemented for type: ${this.type}`);
    }
  }

  private handleTextValidation(input: string): void {
    if (this.maxLength && input.length > this.maxLength) {
      this.isError = true;
      this.helperText = `M√°ximo de ${this.maxLength} caracteres permitido.`;
    } else {
      this.isError = false;
      this.helperText = this.helperTextDefault;
    }
  }

  private handleEmailValidation(input: string): void {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(input)) {
      this.isError = true;
      this.helperText = 'Introduce un correo v√°lido.';
    } else {
      this.isError = false;
      this.helperText = this.helperTextDefault;
    }
  }

  private handlePasswordValidation(input: string): void {
    if (this.maxLength && input.length > this.maxLength) {
      this.isError = true;
      this.helperText = `M√°ximo de ${this.maxLength} caracteres permitido.`;
    } else {
      this.isError = false;
      this.helperText = this.helperTextDefault;
    }
  }
}

import { Component, EventEmitter, Input, Output, signal } from '@angular/core';
import { IconComponent } from '../icon/icon.component';

export type iconShowType = 'left' | 'right' | 'both' | 'any';

@Component({
  selector: 'lib-link',
  imports: [IconComponent],
  templateUrl: './link.component.html',
  styleUrl: './link.component.scss'
})
export class LinkComponent {

  selectIconsToShow = signal<iconShowType>('left');
  @Input({required: true}) text!: string;
  @Input({required: false}) iconLeft?: string;
  @Input({required: false}) iconRight?: string;
  @Input({required: false}) silent?: boolean;
  @Input({required: false}) alternative?: boolean;
  @Input() set iconType(value: iconShowType) {
    this.selectIconsToShow.set(value);
  }
  @Output() linkClicked = new EventEmitter<boolean>();

  handleClick(): void {
    this.linkClicked.emit(true);
  }

}

